select substr('0000000000' || event_id, -10, 10) as '' ,
substr('                    ' || event_type, -20, 20) as '' ,
substr('                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ' || event_data, -1750, 1750) as '' ,
substr('                         ' || origin, -25, 25) as '' ,
substr('                   ' || datetime(time_fired), -19, 19) as '' ,
substr('                   ' || datetime(created), -19, 19) as ''
from events;
